mapper.select:
SELECT COUNT(contentsno)
FROM contents
WHERE cateno= #{cateno}

mapper.update:
UPDATE contents 
SET youtube=#{youtube} 
WHERE contentsno=#{contentsno}

mapper.select:
SELECT COUNT(*) as cnt FROM contents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT contentsno, memberno, cateno, contents_admin, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, r FROM ( SELECT contentsno, memberno, cateno, contents_admin, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube, rownum as r FROM ( SELECT contentsno, memberno, cateno, contents_admin, title, content, recom, viewcnt, commentcnt, passwd, tag, rdate, file1, file1saved, thumb1, size1, youtube FROM contents
WHERE cateno=#{cateno}
WHERE cateno=#{cateno} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY contentsno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM contents
WHERE contentsno = #{contentsno} AND passwd=#{passwd}

mapper.update:
UPDATE contents
SET title = #{title}, content = #{content},  tag = #{tag}
WHERE contentsno = #{contentsno}

UPDATE contents
SET file1 = #{file1}, file1saved = #{file1saved}, thumb1 = #{thumb1}, size1 = #{size1}
WHERE contentsno = #{contentsno}
